CHAT FRAME






ĐÂY LÀ NỘI DUNG ĐOẠN TIN NHẮN 

     KÉO XUỐNG THẺ SCRIPT TÌM YOUR_FIREBASE_URL VÀ THAY BẰNG URL CỦA BẠN, 

      NẾU KHÔNG LOAD ĐƯỢC TIN NHẮN THÌ VÀO RULES > CHO .read và .write = true




//
<!-- Chèn javascript (nếu có) -->

var chatContainer = document.getElementById('chatContainer');
var chatContent = document.getElementById('chatContent');
var chatSelect = document.getElementById('chatSelect');
var xhr = new XMLHttpRequest()
var sData = new Array
var cData = new Array
var html = '', select = '<option selected>Chọn người liên hệ</option>'

function scrollToBottom() {
  chatContainer.scrollTop = chatContent.scrollHeight - chatContainer.clientHeight;
}

function loadUser() {
  xhr.open("GET", "https://YOUR_FIREBASE_URL.firebasedatabase.app/.json", true);
  xhr.onreadystatechange = function () {
    if (xhr.readyState === 4 && xhr.status === 200) {
      sData = JSON.parse(xhr.responseText)
      for (sD in sData) {
        select += `<option value="${sD}">${sD}</option>`
      }
      chatSelect.innerHTML = select
    }
  };
  xhr.send();
}

function loadChat(uid) {
  html = ''
  xhr.open("GET", `https://YOUR_FIREBASE_URL.firebasedatabase.app/${uid}.json`, true);
  xhr.onreadystatechange = function () {
    if (xhr.readyState === 4 && xhr.status === 200) {
      cData = JSON.parse(xhr.responseText)
      for (d in cData) {
        html += `<p><b>${timestampToDateTime(d)}</b>: ${cData[d]}</p>`
      }
      chatContent.innerHTML = html
    }
    scrollToBottom()
  };
  xhr.send();
}

function timestampToDateTime(timestamp) {
  var date = new Date(Number(timestamp));
  var day = date.getDate();
  var month = date.getMonth() + 1;
  var year = date.getFullYear();
  var hours = date.getHours();
  var minutes = date.getMinutes();
  var formattedDateTime = `${day}/${month}/${year} ${hours}h${minutes}p`;
  return formattedDateTime;
}

loadUser()

